document.querySelector(".search-results").addEventListener("click",async a=>{if("anime-title info"!==a.target.className)return;const b=a.target.parentElement;animeId=b.dataset.id;const c=b.dataset.max,d=b.querySelector(".anime-title.info").innerText;await loadEpisodes(c),await loadPlayer({title:d})}),document.querySelector(".episodes-content").addEventListener("click",async a=>{if("button"===a.target.tagName.toLowerCase()){episode=a.target.parentElement.dataset.episode;const b=document.querySelector(".current .title").innerText;await loadPlayer({title:b})}});let typingTimer;const doneTypingInterval=2e3,inputField=document.querySelector("#animeName");inputField.addEventListener("keyup",()=>{clearTimeout(typingTimer),inputField.value&&(typingTimer=setTimeout(search,doneTypingInterval))});